// mobile menu init
function initMobileMenu() {
	var contentBlock = $('#page'),
		mobileMenu = $('#mobile-nav'),
		opener = mobileMenu.find('a.opener'),
		mobileMenuWidth = mobileMenu.outerWidth(),
		activeClass = 'opened',
		animSpeed = 400;

	mobileMenu.css({
		left: -mobileMenuWidth
	});
	opener.on('click', function(e) {
		e.preventDefault();
		toggleMobileMenu();
	});

	function toggleMobileMenu() {
		if (mobileMenu.hasClass(activeClass)) {
			hideMobileMenu();
		} else {
			showMobileMenu();
		}
	}
	
	function showMobileMenu() {
		mobileMenu.addClass(activeClass).stop().animate({
			left: 0
		}, animSpeed);
		contentBlock.stop().animate({
			left: mobileMenuWidth
		}, animSpeed);
	}

	function hideMobileMenu() {
		mobileMenu.removeClass(activeClass).stop().animate({
			left: -mobileMenuWidth
		}, animSpeed);
		contentBlock.stop().animate({
			left: 0
		}, animSpeed);
	}
}

// blog masonry layout
function initMasonry() {
	var containers = jQuery('.blog-masonry .grid-box');
	jQuery(window).on('load', function() {
		containers.masonry({
			itemSelector: '.item',
			transitionDuration: 0
		});
	});
}

